import styled from "styled-components";

import { shopData } from "./ShopData";
import ProductCard from "./ProductCard";
import { Router, Link ,useParams,navigate} from "@reach/router";
const Products = (props) => {
  return (

    <>

      <Title>Welcome to dee food Store</Title>

      <ProductsWrapper>
        {shopData.map((data, index) => (
       
          <Link to={`/${index}`}>
          <ProductCard key={index} {...data} />
          </Link>
        
        ))}
      </ProductsWrapper>


      <Router>
      <ProductsDetails path="/:index" />
      {/*...*/}
    </Router>


    </>
  );
};

export default Products;

function ProductsDetails(){
  const { index } = useParams();
  const products = shopData[index];

  !products? (<h2> products not found</h2>):" ";

  const{name,description,image,rating,price}=products;

  return (<div>
<Wrapper background={image}>
          <AddButton onClick={handleClick} isInCart={isInCart}>
            <p>{isInCart ? "-" : "+"}</p>
          </AddButton>
          <TextContainer>
            <Title>{name}</Title>
            <Subtitle>{rating}.00$</Subtitle>
            <Subtitle>{description}</Subtitle>
            <Subtitle>{price}.00$</Subtitle>
          </TextContainer>
        </Wrapper>

  </div>)



}









const ProductsWrapper = styled.div`
  width: fit-content;
  display: grid;
  grid-template-columns: repeat(4, auto);
  gap: 20px;
`;


const Wrapper = styled.div`
  display: grid;
  align-items: flex-end;
  width: 240px;
  height: 340px;
  border-radius: 20px;
  box-shadow: 0px 20px 40px rgba(52, 53, 99, 0.2),
    0px 1px 3px rgba(0, 0, 0, 0.05);
  background: ${(props) =>
    props.background && `url(${props.background}) center no-repeat`};
  background-size: 300px;
  overflow: hidden;
  position: relative;
`;

const AddButton = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  top: 20px;
  right: 20px;
  width: 20px;
  height: 20px;
  background: ${(props) => (props.isInCart ? "#E55336" : "#60c95d")};
  border-radius: 50%;
  padding: 5px;
  cursor: pointer;

  :hover {
    transform: scale(1.2);
    transition: 1s;
  }

  p {
    font-size: 20px;
    margin: 0;
    color: white;
  }
`;

const TextContainer = styled.div`
  display: grid;
  gap: 10px;
  background: rgba(0, 0, 0, 0.4);
  backdrop-filter: blur(2px);
  width: 100%;
  padding: 20px;
`;

const Title = styled.p`
  font-style: normal;
  font-weight: 500;
  font-size: 24px;
  margin: 0;
  font-weight: bold;
  color: #ffffff;
  margin: 0px;
`;

const Subtitle = styled.p`
  font-weight: normal;
  font-size: 15px;
  color: rgba(255, 255, 255, 0.7);
  margin: 0px;
`;
