import React,{useReducer} from 'react'

function appReducer(state,action){
    switch (action.type) {
        case "add":{
            return[
                ...state,
                {
                    id:Date.now(),
                    text:'',
                    completed:false,
                }
            ]
        }
            
         
    
        default:
            break;
    }
}


const Main = () => {

    const [state,dispatch]=useReducer(appReducer,[])


    return (
        <div>
          <h1>Todo do App</h1>  
{state.map(item=>(
    <div key={item.id}>
        {item.id}
    </div>
))}
        </div>
    )
}

export default Main
