import styled from "styled-components";
import { Router, Link,useParams } from "@reach/router";

import { ShopProvider } from "./ShopContext";
import Products from "./Products";
import Cart from "./Cart";
import { shopData } from './ShopData';

const App = () => {
  return (
    <ShopProvider>
      <Wrapper>
        <TitleWrapper>
          <h1>useReducer Hook</h1>
          <p>
            A <a href="https://designcode.io/">Design+Code</a> tutorial
          </p>
        </TitleWrapper>
        <LinksWrapper>
          <Link to="/">Home</Link>
          <Link to="/cart">Cart</Link>
        </LinksWrapper>
        <Router>
          <Products path="/" />
          <Cart path="/cart" />
          <ProductsDetails path ="/:index"/>
          <ProductsNotFound path ="*"/>
        </Router>
      </Wrapper>
    </ShopProvider>
  );
};

export default App;

function ProductsNotFound() {
  return (
    <div>
      <h1>Not found!</h1>
      <p>Sorry your page was not found!</p>
    </div>
  );
}
function ProductsDetails() {
  const { index } = useParams();
  const meal = shopData[index];

  if (!meal) {
    return <h2> Meal Not Found!</h2>;
  }

  const { name, image,description,rating,price } = meal;

  return (
    <div>

      {/* <img src={image} alt={name} />
      <h1>{name}</h1>
      <h2>{description}</h2>
      <h3>${price}.00</h3>
      <p>{rating}</p>
    </div> */}

